INSERT INTO `tp_cases` (`id`,`main`,`name`,`img`,`content`,`editorValue`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('9','亮化工程','小程序','/tmp/picture/k521kmm8_4hxejdn6rwcg5e12ce50908ad.jpg','&lt;p&gt;这是商业综合体&lt;br&gt;&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;','&lt;p&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体;&quot;&gt;又到了周末啦，早上好！今天给您欣赏的是&lt;/span&gt;&lt;span style=&quot;font-size: 14px; font-family: 宋体;&quot;&gt;商业综合体亮化效果图设计案例。楼体的设计灵感源自于俄罗斯方块，商业区本就是娱乐消费的地方，这种简单的几何图形变换，配合几乎所有人都有玩过的俄罗斯方块效果，让人在远处也能被灯光吸引，感受到这里愉悦轻松的氛围。底部商业区采用暖色调灯光，华丽且温馨，刺激人们消费欲。在一楼与二楼之间再用浅蓝色线条勾边，让商业区层次分明，同时也与主楼体的色调相呼应，整套设计高档华丽又不失活泼！&lt;/span&gt;&lt;/p&gt;','0','0','网站首页-太阳能路灯厂家-保定博楷电子科技有限公司','','保定太阳能路灯,太阳能路灯厂家','1578290813','1578290824','0','0','1');
INSERT INTO `tp_cases` (`id`,`main`,`name`,`img`,`content`,`editorValue`,`home`,`sort`,`title`,`description`,`keywords`,`create_time`,`update_time`,`delete_time`,`isdelete`,`status`) VALUES ('10','亮化工程','案例是海南某商业步行街亮化效果图','/tmp/picture/k521nnew_pprnvude3mo5e12cedd16f4e.jpg','&lt;p&gt;底部步行街区域用3000K色温为主，投光灯打亮立柱，门口两侧建筑洗墙灯自下而上将其结构洗亮区分开来，做到见光不见灯的效果，同时让整体层次更丰富。顶部用湖蓝色洗墙灯勾勒屋顶，与深蓝的天空夜景融为一体。前面小广场区域，用150w图案灯打出图案，增强与路人的互动感让整体更加活泼。重大节日时，中间顶部射灯开启，打亮城市天际线，展示城市的活力，吸引人们前来观赏。&lt;/p&gt;&lt;p&gt;整个步行街区域，冷暖结合，人们身处其中感到放松舒适的同时，整体又能与整个夜景融为一体，自然且温馨。&lt;/p&gt;&lt;p&gt;&lt;br&gt;&lt;/p&gt;','&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; white-space: normal;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;底部步行街区域用&lt;/span&gt;3000K&lt;span style=&quot;font-family: 宋体;&quot;&gt;色温为主，投光灯打亮立柱，门口两侧建筑洗墙灯自下而上将其结构洗亮区分开来，做到见光不见灯的效果，同时让整体层次更丰富。顶部用湖蓝色洗墙灯勾勒屋顶，与深蓝的天空夜景融为一体。前面小广场区域，用&lt;/span&gt;150w&lt;span style=&quot;font-family: 宋体;&quot;&gt;图案灯打出图案，增强与路人的互动感让整体更加活泼。重大节日时，中间顶部射灯开启，打亮城市天际线，展示城市的活力，吸引人们前来观赏。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; font-family: 微软雅黑; white-space: normal;&quot;&gt;&lt;span style=&quot;font-family: 宋体;&quot;&gt;整个步行街区域，冷暖结合，人们身处其中感到放松舒适的同时，整体又能与整个夜景融为一体，自然且温馨。&lt;/span&gt;&lt;/p&gt;','0','0','网站首页-太阳能路灯厂家-保定博楷电子科技有限公司','','保定太阳能路灯,太阳能路灯厂家','1578290913','1578290913','0','0','1');
